import React, { Component } from 'react';
import App from './views/App';
import UIGraph from './UIGraph';
import dynamics from '../../src/index';
import bezierDefaults from './bezierDefaults';

const createCircle = () => {
	const circle = {
		scale: 0,
	};

	dynamics.animate(circle, { scale: 1 }, {
		type: 'spring',
		friction: 300,
		duration: 800,
	});

	return circle;
};

export default class Editor extends Component {
	constructor() {
		super();

		this.state = {
			settings: [],
			circles: [],
			duration: 1000,
			name: 'spring',
		};
	}

	onReady = canvas => {
		this.graph = new UIGraph(canvas);
		this.graph.pointsChanged = this.onUpdate;
		this.onChange();
	}

	onSelect = e => {
		const { value } = e.target;
		this.setState({ name: value });
		this.onChange();
	}

	onChange() {
		const { name } = this.state;
		if (name === 'bezier') {
			this.graph.points = bezierDefaults();
			this.graph.editable = true;
		} else {
			this.graph.points = undefined;
			this.graph.editable = false;
		}

		this.curve = dynamics[name];
		this.values = { ...this.curve.defaults };

		this.setState({ settings: this.createSettings() });
		this.onUpdate();
	}

	onUpdate = () => {
		this.state.settings.forEach(slider => {
			if (slider.options.property === 'duration') {
				this.duration = slider.value();
			} else {
				this.values[slider.options.property] = slider.value();
			}
		});

		if (this.graph.points) {
			this.values.points = this.graph
				.points
				.slice()
				.sort((left, right) => left.x < right.x ? -1 : 1);
		} else {
			this.values.points = undefined;
		}

		this.graph.curve = this.curve(this.values);
		this.graph.draw();

		this.options.onChange && this.options.onChange.call(this);
		this.startAnimationDelayed();
	}

	createSettings() {
		return [
			{
				min: 100,
				max: 5000,
				value: this.state.duration,
				property: 'duration',
				onUpdate: this.onUpdate
			},
			...Object.keys(this.curve.defaults)
				.map(key => {
					const value = this.curve.defaults[key];
					return {
						min: value === 0 ? 0 : 1,
						max: 1000,
						value: this.values[key],
						property: key,
						onUpdate: this.onUpdate,
					};
				})
		];
	}

	startAnimationDelayed() {
		dynamics.clearTimeout(this.delayedAnimation);
		this.delayedAnimation = dynamics.setTimeout(this.startAnimation, 100);
	}

	startAnimation() {
		if (this.circle) {
			const oldCircle = this.circle;
			this.circle = undefined;
			dynamics.animate(oldCircle, {
				opacity: 0
			}, {
				type: dynamics.easeInOut,
				duration: 100,
				complete: () => {
					const demo = document.querySelector('.demo');
					if (oldCircle.parentNode) demo.removeChild(oldCircle);
				}
			});
		}

		const circle = this.circle = createCircle();
		this.setState({ circles: [...this.state.circles, circle] });

		clearTimeout(this.restartAnimationTimeout);
		const initialForce = this.curve(this.values).initialForce;
		const options = {
			...this.values,
			... {
				type: this.curve,
				duration: this.duration,
				delay: 250,
				complete: () => {
					if (this.circle !== circle) return;

					this.circle = undefined;
					this.restartAnimationTimeout = dynamics.setTimeout(this.startAnimation, 300);
					return dynamics.animate(circle, {
						translateX: initialForce ? 0 : 350,
						scale: 0.01
					}, {
						type: dynamics.easeInOut,
						duration: 100,
						delay: 100,
						complete() {
							const demo = document.querySelector('.demo');
							if (circle.parentNode) demo.removeChild(circle);
						}
					});
				}
			}
		};

		dynamics.animate(circle, {
			translateX: 350
		}, options);
	}

	render() {
		return (
			<App
				{ ...this.state }
				onCanvas={ this.onReady }
				onSelect={ this.onSelect }
			/>
		);
	}
};
