import EventEmitter from 'events';
import addUnits from './Interpolable/addUnits';
import createInterpolable from './Interpolable/createInterpolable';
import Matrix from './Matrix';
import transformProperties from './properties/transform';
import types from './types';
import {
	applyProperties,
	parseProperties,
	getCurrentProperties,
	transformValueForProperty,
} from './properties';

const Animation = function(config) {
	const self = new EventEmitter();

	return Object.assign(self, config, {
		// TODO: optimize
		tick(time) {
			if (self.start === undefined) self.start = time;
			const tick = (time - self.start) / self.options.duration;
			const y = self.curve(tick);

			let properties = {};
			if (tick >= 1) {
				if (self.curve.returnsToSelf) {
					properties = self.properties.start;
				} else {
					properties = self.properties.end;
				}
			} else {
				for (const key in self.properties.start) {
					const property = self.properties.start[key];
					properties[key] = property.interpolate(self.properties.end[key], y);
				}
			}

			applyProperties(self.from, properties);

			self.emit('change', self.from);

			if (tick >= 1) self.emit('complete', self.from);

			return tick < 1;
		},
	});
};

export default function(from, to, options) {
	const startProperties = parseProperties(getCurrentProperties(from));
	const properties = parseProperties(to);
	const endProperties = {};
	const transforms = [];
	for (const key in properties) {
		const value = properties[key];
		if (from.style && transformProperties.has(key)) {
			transforms.push([key, value]);
		} else {
			endProperties[key] = createInterpolable(value);
		}
	}

	if (transforms.length > 0) {
		const mat = transforms
			.map(([key, value]) => transformValueForProperty(key, value))
			.join(' ');
		endProperties.transform = Matrix.fromTransform(Matrix.matrixForTransform(mat)).decompose();		
	}
	addUnits(from, endProperties);

	return Animation({
		id: options.id,
		from,
		properties: {
			start: startProperties,
			end: endProperties
		},
		options,
		curve: types[options.type](options),
	});
};
