const defaults = {
	bounciness: 400,
	elasticity: 200,
};

export default Object.assign(function gravity(config = {}) {
	const options = { ...defaults, ...config };
	const bounciness = Math.min(options.bounciness / 1250, 0.8);
	const elasticity = options.elasticity / 1000;
	const gravity = 100;
	const curves = [];
	const L = (function() {
		const b = Math.sqrt(2 / gravity);
		const curve = {
			a: options.returnsToSelf ? 0 : -b,
			b: options.returnsToSelf ? b * 2 : b,
			H: 1,
		};
		while (curve.H > 0.001) {
			const L = curve.b - curve.a;
			curve.a = curve.b;
			curve.b = curve.b + L * bounciness;
			curve.H = curve.H * bounciness * bounciness;
		}
		return curve.b;
	}());

	const getPointInCurve = function(a, b, H, t) {
		const L = b - a;
		const t2 = (2 / L) * t - 1 - (a * 2 / L);
		const c = t2 * t2 * H - H + 1;
		return options.returnsToSelf ? 1 - c : c;
	};

	(function() {
		const b = Math.sqrt(2 / (gravity * L * L));
		const curve = {
			a: options.returnsToSelf ? 0 : -b,
			b: options.returnsToSelf ? b * 2 : b,
			H: 1,
		};
		curves.push(curve);
		let L2 = L;
		while (curve.b < 1 && curve.H > 0.001) {
			L2 = curve.b - curve.a;
			curves.push({
				a: curve.b,
				b: curve.b + L2 * bounciness,
				H: curve.H * elasticity,
			});
		}
	}());

	return Object.assign(function(t) {
		let i = 0;
		let curve = curves[i];
		while (!(t >= curve.a && t <= curve.b)) {
			i += 1;
			curve = curves[i];
			if (!curve) break;
		}

		return !curve ? 
			(options.returnsToSelf ? 0 : 1) :
			getPointInCurve(curve.a, curve.b, curve.H, t);
	}, {
		returnsToSelf: options.returnsToSelf,
	});
}, {
	defaults,
	type: 'gravity',
});
