import Animation from './Animation';
import uniqueId from './utils/uniqueId';
import main from './main';
import types from './types';

const timeouts = new Map();
const animations = new Map();

let slow = 1;
let time = 0;

const stop = id => {
	timeouts.has(id) && timeouts.delete(id);
	animations.has(id) && animations.delete(id);
};

const createAnimation = function(from, to, options) {
	const { id } = options;
	stop(id);

	return Animation(from, to, options);
};

const addTimeout = function(id, fn, delay) {
	timeouts.set(id, {
		fn,
		start: time + delay,
	});
	return id;
};

const callTimeouts = function() {
	const keys = timeouts.keys();
	let iteration = keys.next();
	while (!iteration.done) {
		const key = iteration.value;
		const timeout = timeouts.get(key);
		if (timeout.start <= time) {
			timeout.fn();
			timeouts.delete(key);
		}

		iteration = keys.next();
	}
};

const callAnimations = function() {
	const keys = animations.keys();
	let iteration = keys.next();
	while (!iteration.done) {
		const key = iteration.value;
		const animation = animations.get(key);
		const keep = animation.tick(time);
		if (!keep) animations.delete(key);

		iteration = keys.next();
	}
};

export default Object.assign(main, types, {
	stop,

	tick(delta = 16) {
		time += delta;
		callTimeouts();
		callAnimations();
	},

	setTimeout(fn, delay) {
		const id = uniqueId();
		return addTimeout(id, fn, delay);
	},

	clearTimeout(obj) {
		const id = obj && obj.id !== undefined ? obj.id : obj;
		timeouts.has(id) && timeouts.delete(id);
	},

	animate(from, to, config = {}) {
		const options = {
			type: 'spring',
			duration: 1000,
			delay: 0,
			...config,
		};

		const id = options.id = options.id || uniqueId();
		const delay = options.delay = Math.max(0, options.delay * slow);
		options.duration = Math.max(0, options.duration * slow);

		const animation = createAnimation(from, to, options);
		if (!delay) animations.set(id, animation);
		if (delay) addTimeout(id, () => animations.set(id, animation), delay);

		return animation;
	},

	debug() {
		slow = slow === 1 ? 3 : 1;
		console.log(`dynamics.js: slow animations ${slow === 3 ? 'enabled' : 'disabled'}`);
	},
});
