import React, { Component } from 'react';
import App from './views/App';
import UIGraph from './UIGraph';
import dynamics from '../src/index';
import bezierDefaults from './bezierDefaults';
import createSettings from './createSettings';

export default class Editor extends Component {
	constructor() {
		super();

		this.state = {
			settings: [],
			name: 'spring',
			// curve,
			// values,
		};
	}

	onRef = ref => {
		this.controller = ref;
	}

	onCanvas = canvas => {
		if (!canvas) return;

		this.graph = new UIGraph(canvas);
		this.graph.pointsChanged = this.onUpdate;
		this.onRefresh();
	}

	onSelect = e => {
		const { value } = e.target;
		this.setState({ name: value });
		this.onRefresh(value);
	}

	onRefresh(name = this.state.name) {
		if (name === 'bezier') {
			this.graph.points = bezierDefaults();
			this.graph.editable = true;
		} else {
			this.graph.points = undefined;
			this.graph.editable = false;
		}

		const curve = dynamics[name];
		const values = { ...curve.defaults };

		this.setState({
			curve,
			values,
			settings: createSettings(curve, values, this.onUpdate)
		});

		this.onUpdate();
	}

	onUpdate = (name, value) => {
		if (name) {
			const obj = this.state.settings[name];
			this.setState({
				settings: {
					...this.state.settings,
					[name]: {
						...obj,
						value,
					},
				}
			});
		}

		if (this.graph.points) {
			this.values.points = this.graph
				.points
				.slice()
				.sort((left, right) => left.x < right.x ? -1 : 1);
		} else {
			this.values.points = undefined;
		}

		this.graph.curve = this.state.curve(this.state.values);
		this.graph.draw();

		this.restart();
	}

	restart() {
		clearTimeout(this.delayedAnimation);

		this.delayedAnimation = setTimeout(() => {
			console.log(this.curve);
			console.log(this.values);
			this.controller.start(this.curve, this.values, this.state.settings.duration.value);
		}, 100);
	}

	render() {
		return (
			<App
				{ ...this.state }
				onRef={ this.onRef }
				onCanvas={ this.onCanvas }
				onSelect={ this.onSelect }
				onSettings={ this.onUpdate }
			/>
		);
	}
};
