import React, { Component } from 'react';
import clamp from 'clamp';

const width = 194;
const defaults = {
	min: 0,
	max: 1000,
	value: 10,
};

export default class Slider extends Component {
	constructor(options = {}) {
		super();

		this.state = {
			...defaults,
			...options,
		};

		const { value, min, max } = this.state;
		const left = (value - min) / (max - min) * width;
		this.state.left = left;
	}

	onSliderDown = e => {
		const { layerX } = e.nativeEvent;
		const {
			min,
			max,
		} = this.state;
		const {
			pageX,
			pageY,
		} = e;

		this.setState({
			value: Math.round(layerX / (width + 11) * (max - min) + min),
			left: Math.round(layerX / (width + 11) * width),
		}, () => {
			this.onUpdate();
			this.onControlDown({ pageX, pageY });
		});
	}

	onControlDown = e => {
		e.stopPropagation && e.stopPropagation();

		this.startPoint = [e.pageX, e.pageY];
		this.startLeft = this.state.left;
		this.setState({ dragging: true });

		window.addEventListener('mousemove', this.windowMouseMove);
		window.addEventListener('mouseup', this.windowMouseUp);
	}

	onUpdate() {
		const name = this.props.property;
		const value = this.state.value;
		this.props.onChange(name, value);
	}

	windowMouseMove = e => {
		if (!this.state.dragging) return;

		const {
			min,
			max,
		} = this.state;

		const dX = e.pageX - this.startPoint[0];
		const newLeft = clamp(this.startLeft + dX, 0, width);

		this.setState({
			left: newLeft,
			value: Math.round(newLeft / width * (max - min) + min),
		}, this.onUpdate);
	}

	windowMouseUp = () => {
		this.setState({ dragging: false });
		window.removeEventListener('mousemove', this.windowMouseMove);
		window.removeEventListener('mouseup', this.windowMouseUp);
	}

	render() {
		const { value, dragging, property, left } = this.state;
		return (
			<div>
				<label>
					{ property }
				</label>
				<div className={ `value ${property}` }>
					{ value }
				</div>
				<div
					className={ `slider ${property}` }
					onMouseDown={ this.onSliderDown }
				>
					<div className="bar" />
					<div
						className={ `control ${dragging ? 'highlighted' : ''}` }
						onMouseDown={ this.onControlDown }
						style={ { left } }
					/>
				</div>
			</div>
		);
	}
};
