import Matrix from './index';

// TODO: rework this file

const PI2 = Math.PI * 2;

export default class DecomposedMatrix {
	interpolate(decomposedB, t, only) {
		const decomposedA = this;
		const decomposed = new DecomposedMatrix();
		['translate', 'scale', 'skew', 'perspective'].forEach(key => {
			decomposed[key] = [];
			let i;
			let q;
			let ref1;
			for (i = q = 0, ref1 = decomposedA[key].length - 1; 0 <= ref1 ? q <= ref1 : q >= ref1; i = 0 <= ref1 ? ++q : --q) {
				if (only || (only.includes(key) || only.includes(`${key}${['x', 'y', 'z'][i]}`))) {
					decomposed[key][i] = (decomposedB[key][i] - decomposedA[key][i]) * t + decomposedA[key][i];
				} else {
					decomposed[key][i] = decomposedA[key][i];
				}
			}
		});

		if (only && only.includes('rotate')) {
			const qa = decomposedA.quaternion;
			const qb = decomposedB.quaternion;

			let angle = qa[0] * qb[0] + qa[1] * qb[1] + qa[2] * qb[2] + qa[3] * qb[3];
			if (angle < 0.0) {
				let i;
				let r;
				for (i = r = 0; r <= 3; i = ++r) {
					qa[i] = -qa[i];
				}
				angle = -angle;
			}

			let scale;
			let invscale;
			if (angle + 1.0 > .05) {
				if (1.0 - angle >= .05) {
					const th = Math.acos(angle);
					const invth = 1.0 / Math.sin(th);
					scale = Math.sin(th * (1.0 - t)) * invth;
					invscale = Math.sin(th * t) * invth;
				} else {
					scale = 1.0 - t;
					invscale = t;
				}
			} else {
				qb[0] = -qa[1];
				qb[1] = qa[0];
				qb[2] = -qa[3];
				qb[3] = qa[2];
				scale = Math.sin(PI2 * (.5 - t));
				invscale = Math.sin(PI2 * t);
			}
			decomposed.quaternion = [];

			let i;
			let u;
			for (i = u = 0; u <= 3; i = ++u) {
				decomposed.quaternion[i] = qa[i] * scale + qb[i] * invscale;
			}
		} else {
			decomposed.quaternion = decomposedA.quaternion;
		}
		return decomposed;
	};

	format() {
		return this.toMatrix().toString();
	}

	toMatrix() {
		const decomposedMatrix = this;
		let matrix = Matrix.I(4);

		let i;
		let l;
		for (i = l = 0; l <= 3; i = ++l) {
			matrix.els[i][3] = decomposedMatrix.perspective[i];
		}

		const [
			x,
			y,
			z,
			w,
		] = decomposedMatrix.quaternion;
		const skew = decomposedMatrix.skew;
		const match = [[1, 0], [2, 0], [2, 1]];

		let q;
		for (i = q = 2; q >= 0; i = --q) {
			if (skew[i]) {
				const temp = Matrix.I(4);
				temp.els[match[i][0]][match[i][1]] = skew[i];
				matrix = matrix.multiply(temp);
			}
		}

		matrix = matrix.multiply(new Matrix([[1 - 2 * (y * y + z * z), 2 * (x * y - z * w), 2 * (x * z + y * w), 0], [2 * (x * y + z * w), 1 - 2 * (x * x + z * z), 2 * (y * z - x * w), 0], [2 * (x * z - y * w), 2 * (y * z + x * w), 1 - 2 * (x * x + y * y), 0], [0, 0, 0, 1]]));
		let r;
		for (i = r = 0; r <= 2; i = ++r) {
			let j;
			let u;
			for (j = u = 0; u <= 2; j = ++u) {
				matrix.els[i][j] *= decomposedMatrix.scale[i];
			}
			matrix.els[3][i] = decomposedMatrix.translate[i];
		}
		return matrix;
	}
};
